import React from 'react';
import SignIn from '../Auth/SignIn';
import SignOut from '../Auth/SignOut';
import { auth } from '../FirebaseConfig';
import { useAuthState } from 'react-firebase-hooks/auth';
import { Link } from "react-router-dom";
import './Navbar.css';

export default function Navbar({setShow, show}) {
    const [user] = useAuthState(auth);
    return(
        <>
        <nav className="navbar navbar-expand-lg navbar-dark bg-dark">
            <div className="left-nav">
            <Link to="/" className="navbar-brand">
                <img src="favicon.ico" alt="logo" id="logo"/>
                Yea
            </Link>
            <ul className="navbar-nav">
                    <Link to="/Form" className="nav-link">Form</Link>
            </ul> 
            </div>
            <div className="right-nav">
                <ul className='navbar-nav'>
                <Link to={{
                      pathname: "/Yay",
                      search: `?userUid=${encodeURIComponent(user?.uid)}`,
                    }}
                    className="nav-link"
                  >
                   Tools </Link>
                </ul>
                <ul className="navbar-nav">
                    <Link to={{
                      pathname: "/Profile",
                      search: `?userUid=${encodeURIComponent(user?.uid)}`,
                    }}
                    className="nav-link"
                  >
                    Profile</Link>
                </ul>
                <ul className="sign-button">
                    {user ? <SignOut className="nav-link"/> : <SignIn setShow={setShow} show={show} className="nav-link"/>}
                </ul>
            
                {/* <button className="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarNavDropdown" aria-controls="navbarNavDropdown" aria-expanded="false" aria-label="Toggle navigation">
                    <span className="navbar-toggler-icon"></span>
                </button> */}
            </div>
            {/* <div className="collapse navbar-collapse" id="navbarNavDropdown"> 
            </div> */}
        </nav>
        </>
    )
}
